package dev.codecounty.java.java8.core.basics;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 9;//assignment operator

		i++;// Post Increment
		i--;// Post Decrement

		++i;// Pre increment
		--i;// Pre Decrement
		
//		Shorthand Operator
		i+=7;//i=i+7
		i-=8;
		i%=7;
		
		if(i==5)//Comparison Operator >   <  >=   <=  !=
			System.out.println("Equals 5");
		
//		BitWise Operator
		int x = 8;//TO DO
		
		

	}

}
